DROP TABLE IF EXISTS `mj_admin`;
CREATE TABLE `mj_admin` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL DEFAULT '',
  `password` varchar(255) NOT NULL DEFAULT '',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1正常 2删除',
  `create_time` bigint(20) DEFAULT '0',
  `update_time` bigint(20) DEFAULT '0' COMMENT '修改密码时间',
  `login_time` bigint(20) DEFAULT '0',
  `login_ip` varchar(25) DEFAULT '' COMMENT '登录ip',
  `balance` varchar(255) DEFAULT '0' COMMENT '商户余额',
  `token` varchar(255) DEFAULT '',
  `bank_account` varchar(255) DEFAULT '' COMMENT '银行账户',
  `bank` varchar(255) DEFAULT '' COMMENT '所属银行',
  `branch` varchar(255) DEFAULT '' COMMENT '开户支行',
  `cipher` varchar(255) NOT NULL DEFAULT '' COMMENT '提现密码',
  `payee` varchar(255) DEFAULT '' COMMENT '收款人',
  `payee_id` varchar(255) DEFAULT '' COMMENT '收款人身份证号',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

