  `icon3` varchar(255) DEFAULT NULL,
  `icon4` varchar(255) DEFAULT NULL,
  `status` tinyint(1) DEFAULT '1' COMMENT '1表示正常 2表示删除',
  `create_time` bigint(20) DEFAULT '0',
  `update_time` bigint(20) DEFAULT '0',
  `sale` bigint(20) NOT NULL DEFAULT '0' COMMENT '销售量',
  `detail` text COMMENT '商品介绍',
  PRIMARY KEY (`id`),
  KEY `fk_mj_goods_mj_cate_1` (`cate_id`),
  CONSTRAINT `fk_mj_goods_mj_cate_1` FOREIGN KEY (`cate_id`) REFERENCES `mj_cate` (`id`)
