-- ----------------------------
DROP TABLE IF EXISTS `mj_jisi_wupin`;
CREATE TABLE `mj_jisi_wupin` (
  `wupinid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '祭祀物品id',
  `wupinname` varchar(255) NOT NULL COMMENT '物品名称',
  `wupinphoto` varchar(255) NOT NULL COMMENT '物品图片',
  PRIMARY KEY (`wupinid`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=gbk COMMENT='祭祀用到的物品';

-- ----------------------------
-- Records of mj_jisi_wupin
-- ----------------------------

-- ----------------------------
-- Table structure for mj_mail
-- ----------------------------
DROP TABLE IF EXISTS `mj_mail`;
CREATE TABLE `mj_mail` (
  `mailid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '站内信id',
  `userid` int(10) NOT NULL COMMENT '提交用户id',
  `mailtitle` varchar(30) NOT NULL COMMENT '站内信标题',
  `mailcontent` text NOT NULL COMMENT '站内信内容',
  `mailphoto` varchar(255) NOT NULL COMMENT '站内信图片',
  `addtime` int(10) NOT NULL,
  PRIMARY KEY (`mailid`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=gbk COMMENT='站内信';

-- ----------------------------
-- Records of mj_mail
-- ----------------------------

-- ----------------------------
-- Table structure for mj_msg
-- ----------------------------
DROP TABLE IF EXISTS `mj_msg`;
CREATE TABLE `mj_msg` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL COMMENT '接收人id',
  `send_id` int(11) DEFAULT '0' COMMENT '发送人id',
  `con` varchar(255) NOT NULL COMMENT '消息json',
  `type` int(4) NOT NULL DEFAULT '0' COMMENT '类型 1表示下单消息 2商家发货消息 3订单完成消息',
  `status` int(2) NOT NULL DEFAULT '1' COMMENT '1未读 2已读',
  `create_time` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=41 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of mj_msg
-- ----------------------------

-- ----------------------------
-- Table structure for mj_oauth_user
-- ----------------------------
DROP TABLE IF EXISTS `mj_oauth_user`;
CREATE TABLE `mj_oauth_user` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `uid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '关联的本站用户id',
  `type` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '类型 1：融云   2：友盟',
  `nickname` varchar(30) NOT NULL DEFAULT '' COMMENT '第三方昵称',
  `head_img` varchar(255) NOT NULL DEFAULT '' COMMENT '头像',
  `openid` varchar(40) NOT NULL DEFAULT '' COMMENT '第三方用户id',
  `access_token` varchar(255) NOT NULL DEFAULT '' COMMENT 'access_token token',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '绑定时间',
  `last_login_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '最后登录时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=686 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of mj_oauth_user
-- ----------------------------

-- ----------------------------
-- Table structure for mj_order
-- ----------------------------
DROP TABLE IF EXISTS `mj_order`;
CREATE TABLE `mj_order` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `user_id` bigint(20) NOT NULL COMMENT '用户id',
  `goods_id` int(11) NOT NULL,
  `order_num` varchar(255) NOT NULL COMMENT '订单编号',
  `goods_attr_id` int(11) NOT NULL,
  `attr_info` varchar(500) NOT NULL DEFAULT '' COMMENT 'attr json',
  `title` varchar(255) NOT NULL COMMENT '商品名',
  `price` double(11,2) NOT NULL COMMENT '商品单价',
  `number` int(11) NOT NULL COMMENT '购买数量',
  `name` varchar(255) NOT NULL COMMENT '收货人',
  `phone` varchar(50) NOT NULL COMMENT '联系电话',
  `address` varchar(255) NOT NULL COMMENT '收货地址',
  `status` int(11) NOT NULL DEFAULT '1' COMMENT '1待付款 2已付款 3已发货 4完成  -1用户取消 -2商户取消 -3删除',
  `create_time` int(11) DEFAULT '0',
  `pay_time` int(11) DEFAULT '0',
  `cancel_time` int(11) DEFAULT '0',
  `delete_time` int(11) DEFAULT '0',
  `admin_id` int(11) NOT NULL COMMENT '商户管理员id',
  `nice_pay` varchar(50) DEFAULT '0' COMMENT '实付款',
  `express` varchar(255) DEFAULT '' COMMENT '快递公司',
  `remark` varchar(255) DEFAULT '' COMMENT '用户留言',
  `express_id` varchar(255) DEFAULT '' COMMENT '快递单号',
  `crowd_funding` tinyint(2) NOT NULL DEFAULT '0' COMMENT '是否众筹 0表示不是 1表示是',
  `jiapu_id` int(11) DEFAULT '0' COMMENT '发起众筹用户的家谱id',
  PRIMARY KEY (`id`),
  UNIQUE KEY `order_num` (`order_num`)
) ENGINE=InnoDB AUTO_INCREMENT=50 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of mj_order
-- ----------------------------

-- ----------------------------
-- Table structure for mj_peiou
-- ----------------------------
DROP TABLE IF EXISTS `mj_peiou`;
CREATE TABLE `mj_peiou` (
  `peiouid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '配偶自增id',
  `personnelid` int(11) NOT NULL COMMENT '人物id',
  `perid` int(11) NOT NULL COMMENT '父级id',
  PRIMARY KEY (`peiouid`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=gbk COMMENT='配偶关系表';

-- ----------------------------
-- Records of mj_peiou
-- ----------------------------

-- ----------------------------
-- Table structure for mj_personnel
-- ----------------------------
DROP TABLE IF EXISTS `mj_personnel`;
CREATE TABLE `mj_personnel` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '人物id',
  `name` varchar(255) DEFAULT NULL COMMENT '人员名',
  `sex` int(11) NOT NULL COMMENT '性别 1男2女',
  `mother` int(11) NOT NULL DEFAULT '0' COMMENT '关系人的母亲id 0本人',
  `father` int(11) NOT NULL DEFAULT '0' COMMENT '关系人父亲id',
  `birthday` int(11) NOT NULL DEFAULT '0' COMMENT '生日',
  `death_date` int(11) NOT NULL DEFAULT '0' COMMENT '死亡日期',
  `jiapuid` int(11) NOT NULL DEFAULT '0' COMMENT '家谱id',
  `layer` int(11) NOT NULL DEFAULT '0' COMMENT '层级',
  `wife` int(11) NOT NULL DEFAULT '0' COMMENT '妻子id',
  `husband` int(11) NOT NULL DEFAULT '0' COMMENT '丈夫id',
  `photo` varchar(255) DEFAULT NULL COMMENT '头像',
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  `create_user` int(11) DEFAULT '0' COMMENT '创建者用户id',
  `password` varchar(255) CHARACTER SET utf8 DEFAULT '' COMMENT '验证密码',
  `create_time` int(11) DEFAULT '0',
  `power` int(11) DEFAULT '0' COMMENT '0表示没有权限 1有权修改个人名片 10有权创建家谱成员并修改名片',
  `update_time` int(11) DEFAULT '0',
  `joy` varchar(50) CHARACTER SET utf8 DEFAULT '' COMMENT '职业',
  `detail` text CHARACTER SET utf8 COMMENT '简介',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=171 DEFAULT CHARSET=gbk COMMENT='人物关系表';

-- ----------------------------
-- Records of mj_personnel
-- ----------------------------

-- ----------------------------
-- Table structure for mj_photode
-- ----------------------------
DROP TABLE IF EXISTS `mj_photode`;
CREATE TABLE `mj_photode` (
  `photodeid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '相册详情id',
  `photodedir` varchar(255) NOT NULL COMMENT '路径',
  `photosid` int(11) NOT NULL COMMENT '相册id',
  PRIMARY KEY (`photodeid`)
) ENGINE=MyISAM AUTO_INCREMENT=25 DEFAULT CHARSET=utf8 COMMENT='相册详情表';

-- ----------------------------
-- Records of mj_photode
-- ----------------------------

-- ----------------------------
-- Table structure for mj_photos
-- ----------------------------
DROP TABLE IF EXISTS `mj_photos`;
CREATE TABLE `mj_photos` (
  `photosid` int(255) unsigned NOT NULL AUTO_INCREMENT,
  `photostitle` varchar(255) NOT NULL COMMENT '相册标题',
  `phototime` int(11) NOT NULL COMMENT '创建时间',
  `photocontent` text NOT NULL COMMENT '相册描述',
  `photosdir` varchar(255) NOT NULL COMMENT '展示图地址',
  `userid` int(11) NOT NULL COMMENT '用户id',
  PRIMARY KEY (`photosid`)
) ENGINE=MyISAM AUTO_INCREMENT=12 DEFAULT CHARSET=utf8 COMMENT='相册表';

-- ----------------------------
-- Records of mj_photos
-- ----------------------------

-- ----------------------------
-- Table structure for mj_put_forward
-- ----------------------------
DROP TABLE IF EXISTS `mj_put_forward`;
CREATE TABLE `mj_put_forward` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `admin_id` int(11) NOT NULL,
  `bank` varchar(255) NOT NULL,
  `branch` varchar(255) NOT NULL,
  `bank_account` varchar(255) NOT NULL,
  `payee` varchar(255) NOT NULL,
  `payee_id` varchar(255) NOT NULL,
  `money` varchar(255) NOT NULL COMMENT '提现金额',
  `send_time` int(11) DEFAULT NULL COMMENT '处理时间',
  `create_time` int(11) NOT NULL,
  `status` tinyint(2) DEFAULT '1' COMMENT '1表示申请提现 2提现成功',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of mj_put_forward
-- ----------------------------

-- ----------------------------
-- Table structure for mj_recharge
-- ----------------------------
DROP TABLE IF EXISTS `mj_recharge`;
CREATE TABLE `mj_recharge` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `recharge_num` varchar(255) NOT NULL,
  `user_id` int(11) NOT NULL,
  `create_time` int(11) NOT NULL,
  `pay_time` int(11) DEFAULT '0',
  `status` tinyint(2) NOT NULL DEFAULT '0' COMMENT '0未完成 1完成',
  `money` float(11,2) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of mj_recharge
-- ----------------------------

-- ----------------------------
-- Table structure for mj_sys_config
-- ----------------------------
DROP TABLE IF EXISTS `mj_sys_config`;
CREATE TABLE `mj_sys_config` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='系统 配置表';

