-- Records of mj_sys_config
-- ----------------------------

-- ----------------------------
-- Table structure for mj_sys_log
-- ----------------------------
DROP TABLE IF EXISTS `mj_sys_log`;
CREATE TABLE `mj_sys_log` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `uid` int(10) unsigned NOT NULL COMMENT '操作人的ID',
  `contents` varchar(300) DEFAULT NULL COMMENT '操作内容',
  `date` int(11) unsigned DEFAULT NULL COMMENT '操作时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=252 DEFAULT CHARSET=utf8 COMMENT='系统日志表';

-- ----------------------------
-- Records of mj_sys_log
-- ----------------------------

-- ----------------------------
-- Table structure for mj_user
-- ----------------------------
DROP TABLE IF EXISTS `mj_user`;
CREATE TABLE `mj_user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 NOT NULL COMMENT '用户名',
  `password` varchar(255) CHARACTER SET utf8 NOT NULL COMMENT '密码',
  `create_time` int(11) DEFAULT NULL,
  `type` tinyint(1) DEFAULT '1' COMMENT '1表示普通会员',
  `nickname` varchar(255) CHARACTER SET utf8 DEFAULT NULL COMMENT '真实名字',
  `photo` varchar(255) CHARACTER SET utf8 DEFAULT NULL COMMENT '头像',
  `birthday` int(11) DEFAULT NULL,
  `sex` tinyint(1) DEFAULT '1' COMMENT '1表示男生 2表示女生',
  `jiapu_id` int(11) DEFAULT '0' COMMENT '正在使用的家谱id',
  `status` tinyint(1) DEFAULT '1' COMMENT '1表示正常 2表示删除',
  `token` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `login_time` int(11) DEFAULT NULL,
  `login_ip` varchar(15) CHARACTER SET utf8 DEFAULT NULL,
  `address` varchar(255) CHARACTER SET utf8 DEFAULT '' COMMENT '详细地址',
  `phone` bigint(20) DEFAULT NULL,
  `balance` double(11,2) DEFAULT '0.00' COMMENT '余额',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=latin1 ROW_FORMAT=COMPACT;

-- ----------------------------
-- Records of mj_user
-- ----------------------------

-- ----------------------------
-- Table structure for mj_user2
-- ----------------------------
DROP TABLE IF EXISTS `mj_user2`;
CREATE TABLE `mj_user2` (
  `userid` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `username` varchar(255) NOT NULL COMMENT '用户名',
  `password` varchar(255) NOT NULL COMMENT '密码',
  `regtime` int(11) NOT NULL COMMENT '注册时间',
  `usertype` int(255) NOT NULL DEFAULT '0' COMMENT '会员0否，1是',
  `nickname` varchar(255) NOT NULL COMMENT '真实姓名',
  `photo` varchar(255) NOT NULL COMMENT '头像',
  `userinter` text NOT NULL COMMENT '个人简介',
  `birthday` int(255) NOT NULL COMMENT '生日',
  `sex` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1表示男生 2表示女生',
  `jiapu_id` int(11) NOT NULL DEFAULT '0',
  `jiapu_permission` int(11) NOT NULL DEFAULT '0' COMMENT '控制家谱的权限 0表示没权限 1表示有',
  PRIMARY KEY (`userid`),
  UNIQUE KEY `username` (`username`)
) ENGINE=MyISAM AUTO_INCREMENT=29 DEFAULT CHARSET=utf8 COMMENT='前台用户表';

-- ----------------------------
-- Records of mj_user2
-- ----------------------------

-- ----------------------------
-- Table structure for mj_zhuanji
-- ----------------------------
DROP TABLE IF EXISTS `mj_zhuanji`;
CREATE TABLE `mj_zhuanji` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '传记id',
  `user_id` int(11) NOT NULL COMMENT '用户id',
  `brief` varchar(255) NOT NULL DEFAULT '' COMMENT '简介',
  `title` varchar(255) NOT NULL COMMENT '传记标题',
  `detail` text NOT NULL COMMENT '传记内容',
  `create_time` int(11) NOT NULL COMMENT '发表时间',
  `photo` varchar(255) NOT NULL COMMENT '传记封面',
  `status` tinyint(2) NOT NULL DEFAULT '1' COMMENT '1正常 2删除',
  `update_time` int(11) NOT NULL DEFAULT '0' COMMENT '传记的品论数',
  `type` int(10) NOT NULL DEFAULT '1' COMMENT '传记类型 1家族人物',
  `jiapu_id` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=21 DEFAULT CHARSET=utf8 COMMENT='传记';

-- ----------------------------
-- Records of mj_zhuanji
-- ----------------------------
